export default class{constructor(e){if(this.container=e,this.prefersReducedMotion=!1,"undefined"!=typeof window&&window.matchMedia){this.prefersReducedMotion=window.matchMedia("(prefers-reduced-motion: reduce)").matches;window.matchMedia("(prefers-reduced-motion: reduce)").addEventListener("change",e=>{this.prefersReducedMotion=e.matches})}this.messages={dark:"Sometimes we need to embrace the shadows. Here are some books that understand the beauty in darkness.",cozy:"Time to curl up with something warm and comforting. These books are like a hug for your soul.",heartbreaking:"It's okay to feel deeply. These stories honor your emotions and offer paths toward healing.",motivational:"You're ready to conquer the world! These books will fuel your fire and inspire your journey.",chaotic:"EMBRACE THE CHAOS! Reality is overrated anyway. These books are delightfully unhinged. ðŸ¤ª"}}getMessage(e){return this.messages[e]||""}showMessage(e){const t=this.getMessage(e);t?(this.container.classList.remove("message-panel--animate"),this.container.textContent=t,this.prefersReducedMotion?(this.container.style.opacity="1",this.container.style.transform="translateY(0)"):(this.container.offsetWidth,this.container.classList.add("message-panel--animate"))):this.container.innerHTML=""}}